diamond_pushups = Exercise.new(name: "Diamond Pushups", category: "Arms", equipment: "None", description: "")

barbell_press= Exercise.new(name: "Barbell Press", category: "Shoulders", equipment: "Barbell", description: "")
military_press= Exercise.new(name: "Military Press", category: "Shoulders", equipment: "Dumbbells", description: "")
raises= Exercise.new(name: "Lateral Raise", category: "Shoulders", equipment: "Dumbbells", description: "")
arnold_press= Exercise.new(name: "Arnold Press", category: "Shoulders", equipment: "Dumbbells", description: "")
cable_reverse = Exercise.new(name: "Cable Reverse Fly", category: "Shoulders", equipment: "Cables", description: "")
shoulder_press = Exercise.new(name: "Inverted Shoulder Press", category: "Shoulders", equipment: "None", description: "")
front_raises= Exercise.new(name: "Front Raise", category: "Shoulders", equipment: "Dumbbells", description: "")

deadlift= Exercise.new(name: "Deadlift", category: "Back", equipment: "Barbell", description: "")
single_row= Exercise.new(name: "Single-Arm Row", category: "Back", equipment: "Dumbbells", description: "")
row= Exercise.new(name: "Barbell Row", category: "Back", equipment: "Barbell", description: "")
lat_pulldown= Exercise.new(name: "Lat Pull-Down", category: "Back", equipment: "Lat Machine", description: "")
pullup= Exercise.new(name: "Pull-up", category: "Back", equipment: "Pull-up bar", description: "")
dumbbell_row = Exercise.new(name: "Chest Supported Dumbbell Row", category: "Back", equipment: "Dumbbells", description: "")
inverted_row= Exercise.new(name: "Inverted Row", category: "Back", equipment: "Barbell", description: "")
shrugs= Exercise.new(name: "Dumbbell Shrugs", category: "Back", equipment: "Dumbbells", description: "")

legpress= Exercise.new(name: "Leg Press", category: "Legs", equipment: "Press Machine", description: "Make sure your lower back stays pressed against the bench pad at all times throughout the exercise, and you keep your chest high. Release the safety and bend your knees to lower the platform until you feel your glutes wanting to lift off the bench. In a steady motion, push the platform back up to the top without locking your knees.")
squats= Exercise.new(name: "Barbell Squats", category: "Legs", equipment: "Barbell", description: "Holding the barbell across your upper back, stand with your feet shoulder width apart. Begin to lower your body down as if you were sitting in a chair until your thighs are parallel with the floor. Drive the weight up to push yourself back up to the starting position.")
jump_squat= Exercise.new(name: "Jump Squat", category: "Legs", equipment: "None", description: "Stand with your feet shoulder width apart and lower into a squat position until your thighs are parallel to the floor. In an explosive motion, jump up as high as you can, landing with soft knees to avoid injury.")
hack_squat= Exercise.new(name: "Hack Squat", category: "Legs", equipment: "Hack Squat Machine", description: "Place your shoulders and back against the pads with your feet planted flat and firm on the plate. Engage your core, release the safety, and begin to lower yourself down until your thighs are parallel to the platform. Hold for a second, and then drive your heels into the platform to propel yourself back up to the starting position.")
extensions= Exercise.new(name: "Leg Extensions", category: "Legs", equipment: "Leg Extension Machine", description: "Hold onto the side bars for leverage and make sure the rest of your body is stationary so you only engage the quads to lift the weight. Exhale as you pull the weights up and pause for a second before you inhale and bring the weights back down.")
leg_curls = Exercise.new(name: "Lying Leg Curls", category: "Legs", equipment: "Leg Curl Machine", description: " Position your legs so the knees are right below the edge of the bench. Exhale as you curl the lower legs until the hamstrings are fully flexed. Pause at the top and then inhale as you bring your legs back down to the starting position for a full extension.")
lunges= Exercise.new(name: "Lunges", category: "Legs", equipment: "none", description: "Make sure that throughout the lunge, your knee on the front leg never goes past your toes. Engage your core to keep your body steady. Step forward with one foot while bending at the knee and lowering your torso towards the floor. Stop just short of your back knee touching the floor. Push your front foot into the floor as you rise up, bringing your back leg forward to the standing position.")
reverse_lunges= Exercise.new(name: "Reverse Lunges", category: "Legs", equipment: "None", description: "Tighten your core to maintain stability throughout the exercise. Step back with one foot and begin to lower your body until your front thigh is parallel to the floor and your back knee is almost touching the floor. Rise up and step back to the starting position.")
calf_raises= Exercise.new(name: "Calf Raises", category: "Legs", equipment: "none", description: "Stand on the machine or box with the balls of your feet at the edge of the platform and the heels of your feet are hanging off. Breathe out as you rise up, using your calves to push up the weight on the machine or the dumbbells in your hands.")

plank= Exercise.new(name: "Plank", category: "Abs", equipment: "None", description: "Lying face down on the ground, prop yourself up into a forearm plank position. Make sure your elbows are aligned underneath your shoulders, and that your hands are balled up in fists. Your forearms should be parallel to one another.")
side_bend= Exercise.new(name: "Dumbbell Side Bend", category: "Abs", equipment: "Dumbbells", description: "Stand with your feet hip-width apart and hold a dumbbell in your right hand, palm facing inwards towards the torso. Keep your back straight, activate your core, and then bend to the side as far as possible—but only at the waist. Hold for one second at the bottom of your range of motion, and return to start for one rep.")
side_plank= Exercise.new(name: "Side Plank", category: "Abs", equipment: "None", description: "Lie on either side with your forearm flat on the floor, elbow under shoulder, and both legs extended. Body should form a straight line from head to feet, which are either stacked or staggered. Engage your core and lift hips up.")
reverse_crunch= Exercise.new(name: "Reverse Crunch", category: "Abs", equipment: "None", description: "Start lying on back, legs lifted so thighs are perpendicular to ground and knees are bent. Pressing lower back into mat, curl knees into chest to lift hips off the ground. Return to start with control.")
heel_taps= Exercise.new(name: "Heel Taps", category: "Abs", equipment: "None", description: "Start on back with knees bent and feet on ground, hands by hips. Lift chest toward the ceiling, keeping neck relaxed and bringing shoulder blades off the ground. Tap right hand to outside of right ankle, flexing right obliques in the process. Come back to center, keeping shoulders off ground, then tap left hand to outside of left ankle.")
ab_roller= Exercise.new(name: "Ab Roller", category: "Abs", equipment: "Ab Roller", description: "Slowly extend the ab roller away from you while keeping a straight back and the core engaged. Go until you are parallel with the floor and slowly return to the start position.")
machine_crunch= Exercise.new(name: "Machine Crunch", category: "Abs", equipment: "Ab Crunch Machine", description: "Grab the handles and slowly bring your arms to your knees with your abs engaged. Hold briefly and slowly return the start position.")
v_up= Exercise.new(name: "V-Up", category: "Abs", equipment: "None", description: "Start lying on back with legs extended and arms by sides, both on mat. In one movement, lift upper body, arms, and legs, coming to balance on tailbone, forming a 'V' shape with body. Lower body back down.")

stairmaster= Exercise.new(name: "Stair Master", category: "Cardio", equipment: "StairMaster", description: "Climb stairs with your core engaged and without leaning on the handles.")
jogging= Exercise.new(name: "Jogging", category: "Cardio", equipment: "Treadmill", description: "Jog and try to drown the pain with music or thoughts.")
burpees= Exercise.new(name: "Burpees", category: "Cardio", equipment: "none", description: "Stand with your feet shoulder-width apart, weight in your heels, and your arms at your sides. Push your hips back, bend your knees, and lower your body into a squat. Place your hands on the floor directly in front of, and just inside, your feet. Shift your weight onto your hands. Jump your feet back to softly land on the balls of your feet in a plank position. Jump your feet back so that they land just outside of your hands. Reach your arms over head and explosively jump up into the air.")
inclined_walking= Exercise.new(name: "Inclined Walking", category: "Cardio", equipment: "Treadmill", description: "Set treadmill to a moderate incline and speed that is a fast walk.")
bike= Exercise.new(name: "Stationary Bike", category: "Cardio", equipment: "Stationary Bike", description: "Set the resistance to your liking and pedal until you can't anymore.")
sprints= Exercise.new(name: "Sprinting", category: "Cardio", equipment: "none", description: "Sprint 40-50 yards at full speed and rest for a minute.")

