PlanExercise.create(plan: plan3, exercise: legpress, sets: "4 sets of 6-8 reps")
PlanExercise.create(plan: plan3, exercise: hack_squat, sets: "4 sets of 6-8 reps")
PlanExercise.create(plan: plan3, exercise: extensions, sets: "4 sets of 10-12 reps")
PlanExercise.create(plan: plan3, exercise: leg_curls, sets: "4 sets of 10-12 reps")

plan4= Plan.create(name: "Toned Core", difficulty: "Beginner", commitment: "3-4 times per week", image: "toned_core.jpg", description: "This plan is for those who want to tone their mid-section and bring out ab definition.")
PlanExercise.create(plan: plan4, exercise: v_up, sets: "3 sets of 12-15 reps")
PlanExercise.create(plan: plan4, exercise: reverse_crunch, sets: "3 sets of 15-20 reps")
PlanExercise.create(plan: plan4, exercise: heel_taps, sets: "3 sets of 20-25 reps on each side")
PlanExercise.create(plan: plan4, exercise: side_plank, sets: "3 sets of 30 second holds on each side")
PlanExercise.create(plan: plan4, exercise: plank, sets: "3 sets of 1 minute holds")
PlanExercise.create(plan: plan4, exercise: jogging, sets: "20-45 minutes")

plan5= Plan.create(name: "Shredded Core", difficulty: "Moderate", commitment: "2-3 times per week", image: "shredded.jpg", description: "This plan is for those who want to build ab muscle and condition their abs.")
PlanExercise.create(plan: plan5, exercise: ab_roller, sets: "3 sets of 12-15 reps")
PlanExercise.create(plan: plan5, exercise: reverse_crunch, sets: "3 sets of 15-20 reps")
PlanExercise.create(plan: plan5, exercise: machine_crunch, sets: "3 sets of 15-20 reps")
PlanExercise.create(plan: plan5, exercise: side_bend, sets: "3 sets of 12-15 reps on each side")
PlanExercise.create(plan: plan5, exercise: plank, sets: "3 sets of 1 minute holds")
PlanExercise.create(plan: plan5, exercise: sprints, sets: "6-8 spints at 100%")

plan6= Plan.create(name: "Greek God Chest", difficulty: "Advanced", commitment: "1-2 times per week", image: "greed_chest.jpg", description: "This plan is for those who want to add muscle to their upper and middle chest. ")
PlanExercise.create(plan: plan6, exercise: bench_press, sets: "5 sets of 5-6 reps")
PlanExercise.create(plan: plan6, exercise: incline_bench, sets: "4 sets of 6-8 reps")
PlanExercise.create(plan: plan6, exercise: dumbbell_press, sets: "4 sets of 6-8 reps")
PlanExercise.create(plan: plan6, exercise: chest_press, sets: "4 sets of 10-12 reps")
PlanExercise.create(plan: plan6, exercise: cable_fly, sets: "4 sets of 10-12 reps")

plan7= Plan.create(name: "Toned Chest", difficulty: "Advanced", commitment: "1-2 times per week", image: "toned_chest.jpeg", description: "This plan is for those who want to get a defined chest and build chest strength.")
PlanExercise.create(plan: plan7, exercise: bench_press, sets: "4 sets of 10-12 reps")
PlanExercise.create(plan: plan7, exercise: dumbbell_press, sets: "4 sets of 10-12 reps")
PlanExercise.create(plan: plan7, exercise: dumbbell_fly, sets: "4 sets of 10-12 reps")
PlanExercise.create(plan: plan7, exercise: cable_fly, sets: "4 sets of 10-12 reps")
PlanExercise.create(plan: plan7, exercise: pushup, sets: "3 sets to failure")

plan8= Plan.create(name: "Push-Up Overload", difficulty: "Beginner", commitment: "2-3 times per week", image: "pushups.jpg", description: "This plan is for anyone that needs a chest workout without weights. This plan will help build strength and define the chest muscles.")
PlanExercise.create(plan: plan8, exercise: pushup, sets: "500 pushups throughout the day. However many sets it takes.")



